mapper.insert:
insert into TB_BOARD (

) values (
	  NEXTVAL(TB_BOARD_SEQ)
	, #{title}
	, #{content}
	, #{writer}
	, 0
	, 'N'
	, 'N'
	, 'N'
	, now()
	, null
	, null
)

mapper.update:
UPDATE
	TB_Board
SET
	  update_time = sysdate
  , title = #{title}
  , content = #{content}
  , writer = #{writer}
  , notice_yn = NVL(#{noticeYn, jdbcType=VARCHAR}, 'N')
  , secret_yn = NVL(#{secretYn, jdbcType=VARCHAR}, 'N')
WHERE
	idx = #{idx}
